mapper.select:
SELECT id, title, nickname, update_date
FROM baseboardlist
ORDER BY update_date DESC
OFFSET #{offset}
LIMIT #{listCnt};

SELECT nickname, title, content, update_date
FROM baseboardlist
WHERE 1=1 AND id = #{id}
LIMIT 1;

SELECT reply_id, post_id, parent_id, is_parent, content, nickname, update_date
FROM baseboardreplylist
WHERE post_id = #{postId}
ORDER BY parent_id, reply_id;
